import React, { FunctionComponent, useMemo, useState } from 'react'
import classNames from 'classnames'
import { ArrowDown } from '@nutui/icons-react-taro'
import { View } from '@tarojs/components'
import Popup from '@/packages/popup/index.taro'
import { PopupProps } from '@/packages/popup/popup.taro'
import { useConfig } from '@/packages/configprovider/configprovider.taro'
import { ComponentDefaults } from '@/utils/typings'

export interface NumberKeyboardProps extends PopupProps {
  visible: boolean
  confirmText?: string
  type: 'default' | 'rightColumn'
  custom: Array<string>
  random: boolean
  onChange?: (value: string) => void
  onDelete?: () => void
  onClose: () => void
  onConfirm?: () => void
}
const defaultProps = {
  ...ComponentDefaults,
  visible: false,
  type: 'default',
  custom: [],
  random: false,
  onClose: () => {},
} as unknown as NumberKeyboardProps

export const NumberKeyboard: FunctionComponent<
  Partial<NumberKeyboardProps> &
    Omit<React.HTMLAttributes<HTMLDivElement>, 'onChange' | 'onClick' | 'title'>
> = (props) => {
  const { locale } = useConfig()
  const {
    title,
    confirmText,
    visible,
    type,
    custom,
    random,
    style,
    className,
    onChange,
    onDelete,
    onClose,
    onConfirm,
    ...rest
  } = props
  const classPrefix = 'nut-numberkeyboard'

  const getBasicKeys = () => {
    const keys = new Array(9).fill(0).map((_, index) => {
      return {
        id: String(index + 1),
        type: 'num',
      }
    })
    return random ? keys.sort(() => (Math.random() > 0.5 ? 1 : -1)) : keys
  }

  const getCustomKeys = () => {
    const customKeys = [
      { id: 'close', type: 'close' },
      { id: '0', type: 'num' },
      { id: 'delete', type: 'delete' },
    ]
    if (!custom) return customKeys
    if (custom.length > 0) {
      customKeys[0] = { id: custom[0], type: 'custom' }
    }
    if (custom.length > 1) {
      customKeys[2] = { id: custom[1], type: 'custom' }
    }
    return customKeys
  }

  const keysList = useMemo(() => {
    return [...getBasicKeys(), ...getCustomKeys()]
  }, [type, random, custom])

  const DeleteIcon = () => {
    const style = {
      background: `url('data:image/png;base64,') no-repeat center`,
      backgroundSize: '100% 100%',
      width: '28px',
      height: '24px',
    }
    return <View style={style} />
  }

  const NumberKeyboardKey = (props: { item: any }) => {
    const { item } = props
    const [active, setActive] = useState(false)
    const onTouchStart = () => {
      setActive(true)
    }
    const onTouchEnd = (item: { id: string; type: string }) => {
      setActive(false)
      if (item.type === 'num' || item.type === 'custom') {
        onChange && onChange(item.id)
      }
      if (item.type === 'close') {
        onClose && onClose()
      }
      if (item.type === 'delete') {
        onDelete && onDelete()
      }
      if (item.type === 'confirm') {
        onConfirm && onConfirm()
      }
    }
    return (
      <div key={item.id} className={`${classPrefix}-body-wrapper`}>
        <div
          className={classNames({
            key: true,
            active,
            close: item.type === 'close',
            delete: item.type === 'delete',
            confirm: item.type === 'confirm',
          })}
          onTouchStart={() => onTouchStart()}
          onTouchEnd={() => onTouchEnd(item)}
          onTouchCancel={() => onTouchEnd(item)}
        >
          {(item.type === 'num' || item.type === 'custom') && (
            <div>{item.id}</div>
          )}
          {item.type === 'delete' && <DeleteIcon />}
          {item.type === 'close' && <ArrowDown size={18} />}
          {item.type === 'confirm' && <>{confirmText || locale.done}</>}
        </div>
      </div>
    )
  }

  return (
    <Popup
      visible={visible}
      position="bottom"
      onOverlayClick={onClose}
      onCloseIconClick={onClose}
      zIndex={9999}
      overlayStyle={{ backgroundColor: 'rgba(0, 0, 0, 0)' }}
      {...rest}
    >
      <div className={classNames(classPrefix, className)} style={style}>
        {title && (
          <div className={`${classPrefix}-header`}>
            <div className={`${classPrefix}-header-title`}>{title}</div>
            {type === 'default' && (
              <span
                className={`${classPrefix}-header-close`}
                onClick={onConfirm}
              >
                {locale.done}
              </span>
            )}
          </div>
        )}
        <div className={`${classPrefix}-body`}>
          <div className={`${classPrefix}-body-keys`}>
            {keysList?.map((item: any) => {
              return <NumberKeyboardKey key={item.id} item={item} />
            })}
          </div>
          {type === 'rightColumn' && (
            <div className={`${classPrefix}-sidebar`}>
              <NumberKeyboardKey
                key="delete"
                item={{
                  id: 'delete',
                  type: 'delete',
                }}
              />
              <NumberKeyboardKey
                key="confirm"
                item={{
                  id: 'confirm',
                  type: 'confirm',
                }}
              />
            </div>
          )}
        </div>
      </div>
    </Popup>
  )
}

NumberKeyboard.defaultProps = defaultProps
NumberKeyboard.displayName = 'NutNumberKeyboard'
